import React from 'react';

function DateCalendar() {
    const runCallback = (cb) => {
        return cb();
    };
    return (
        <div className="calendar" style="display: block;">
            <div className="calendar__main">
                <div className="calendar__content">
                    {
                        runCallback(() => {
                            const row = [];
                            for (var i = 0; i < 5; i++) {
                                row.push(
                                    <div className="calendar__wrapper">
                                        <div className="calendar__month">{month} {year}</div>
                                        <table className="calendar__dates">
                                            <thead className="calendar__row">
                                                <tr>
                                                    <th className="calendar__day-name">Mo</th>
                                                    <th className="calendar__day-name">Tu</th>
                                                    <th className="calendar__day-name">We</th>
                                                    <th className="calendar__day-name">Th</th>
                                                    <th className="calendar__day-name">Fr</th>
                                                    <th className="calendar__day-name">Su</th>
                                                    <th className="calendar__day-name">Sa</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {/*<tr className="calendar__row">*/}
                                                    {
                                                        runCallback(() => {
                                                            let day = 1;
                                                            let dayOfTheWeek = 1;
                                                            let running = true;
                                                            let month = date("m") + i;
                                                            if (month > 12) {
                                                                month = 12;
                                                            }
                                                            let daysInMonth = cal_days_in_month(CAL_GREGORIAN, month, date("Y"));
                                                            const row = [];
                                                            for (var i = 0; i < 5; i++) {
                                                                row.push(<p key={i}>{i}</p>);
                                                            }
                                                            while (running) {
                                                                if (day >= daysInMonth) {
                                                                    running = false;
                                                                }
                                                                if (dayOfTheWeek > 7) {
                                                                    dayOfTheWeek = 1;
                                                                    row.push(
                                                                        <div>
                                                                            <td></td>
                                                                            <tr className="calendar__row"></tr>
                                                                        </div>
                                                                    );
                                                                    getWeekday = date('N', strtotime(date("Y-" + (date("m") + i) + "-" + day)));
                                                                    var classVar = null;
                                                                    if (getWeekday == dayOfTheWeek) {
                                                                        if (strtotime(date("Y-" + (date("m") + i) + "-" + day)) >= strtotime(date("Y-m-d"))) {
                                                                            if (strtotime(date("Y-" + (date("m") + i) + "-" + day)) == strtotime(date("Y-m-d"))) {
                                                                                classVar = "calendar__date calendar__date--today";
                                                                            } else {
                                                                                classVar = "calendar__date";
                                                                            }
                                                                        } else {
                                                                            classVar = "calendar__date calendar__date--disabled";
                                                                        }
                                                                        row.push(
                                                                            < td className={classVar} data-ref="calendar-date" data-date="'.$year.'-'.$monthNumeric.'-'.$day.'" data-days={daysInMonth} >
                                                                                <span aria-label="'.$day.' '.$month.' '.$year.'">
                                                                                    <span aria-hidden="true">{day}</span>
                                                                                </span>
                                                                            </td>
                                                                        );
                                                                        day++;
                                                                    } else {
                                                                        row.push(
                                                                            < td className="calendar__date--empty" ></td>
                                                                        );
                                                                    }
                                                                    dayOfTheWeek++;
                                                                }
                                                            }
                                                            row.push("</td>");
                                                            return row;
                                                        })
                                                    }
                                            </tbody>
                                        </table>
                                    </div>
                                );
                            }
                            return row;
                        })
                    }
                </div>
                <div className="calendar__display">Check-in Date - Check-out Date</div>
            </div>
        </div >
    )
}

export default DateCalendar;
